mapper.select:
select d.id as did, d.name as dname,c.id as cid, c.name as cname, c.address as caddress, c.phone as cphone
from department d,
company c
where d.cId = c.id

    and cId = #{cId}

select d.id as did, d.name as dname, c.id as cid, c.name as cname, c.address as caddress, c.phone as cphone
from department d,
     company c
where d.cId = c.id
  and d.id = #{id}

mapper.update:
update department
set name = #{name},
    cId  = #{cId}
where id = #{id}
